SELECT D.address, D.birthday, D.city, D.company_code companyCode, D.company_name companyName, D.detail_code detailCode, D.tax_no taxNo, D.form_code formCode, D.gender, D.id_card_no idCardNo, D.job, D.location_id locationId, D.nation, D.org_code orgCode, D.person_name personName, D.region, D.task_code taskCode, D.tel, D.working, D.detail_status detailStatus, R.detail_code detailCode, R.detail_type detailType, R.pictures, R.positive, R.test_tube_no testTubeNo, R.result_status resultStatus, T.task_name taskName FROM biz_person_task T inner join biz_person_task_detail D on D.task_code=T.task_code left join biz_person_task_detail_result R on D.detail_code = R.detail_code WHERE 1=1
and T.task_code = #{param.taskCode}
and T.task_name like concat('%',#{param.taskName},'%')
and T.task_status = #{param.taskStatus}
and T.start_date <= #{param.startDate}
and T.end_date >= #{param.endDate}
and T.id_card_no >= #{param.idCardNo}
and exists (select 1 from biz_person_task_org o where o.task_code=T.task_code and o.org_code in
#{method.code} )
and T.created_by = #{param.userDataAccount}

SELECT D.address, D.birthday, D.city, D.company_code companyCode, D.company_name companyName, D.detail_code detailCode, D.tax_no taxNo, D.form_code formCode, case D.gender when 1 then '女' when 0 then '男' end gender, D.id_card_no idCardNo, D.job, D.location_id locationId, D.nation, D.org_code orgCode, D.person_name personName, D.region, D.task_code taskCode, D.tel, D.working, D.detail_status detailStatus, R.detail_code detailCode, R.detail_type detailType, R.pictures, R.positive, R.test_tube_no testTubeNo, R.result_status resultStatus, T.task_name taskName FROM biz_person_task T inner join biz_person_task_detail D on D.task_code=T.task_code left join biz_person_task_detail_result R on D.detail_code = R.detail_code WHERE 1=1
and T.task_code = #{param.taskCode}
and T.task_name like concat('%',#{param.taskName},'%')
and T.task_status = #{param.taskStatus}
and T.start_date <= #{param.startDate}
and T.end_date >= #{param.endDate}
and T.id_card_no >= #{param.idCardNo}
and exists (select 1 from biz_person_task_org o where o.task_code=T.task_code and o.org_code in
#{method.code} )
and T.created_by = #{param.userDataAccount}
