mapper.select:
SELECT D.address, D.city, D.company_code companyCode, D.company_name companyName, D.detail_code detailCode, D.tax_code taxCode, D.form_code formCode, D.tag_id tagId, D.tag_name tagName, D.org_code orgCode, D.task_code taskCode, D.tel, D.detail_status detailStatus, R.detail_code detailCode, R.detail_type detailType, R.pictures, R.positive, R.test_tube_no testTubeNo, R.result_status resultStatus, T.task_name taskName FROM biz_environment_task T inner join biz_environment_task_detail D on D.task_code=T.task_code left join biz_environment_task_detail_result R on (D.detail_code = R.detail_code and R.deleted=0) WHERE D.deleted=0
and T.task_code = #{param.taskCode}
and T.task_name like concat('%',#{param.taskName},'%')
and T.task_status = #{param.taskStatus}
and T.start_date <= #{param.startDate}
and T.end_date >= #{param.endDate}
and exists (select 1 from biz_person_task_org o where o.task_code=T.task_code and o.org_code in
#{method.code} )
and T.created_by = #{param.userDataAccount}
order by T.created_date desc,t.modified_date desc

SELECT D.address, D.city, D.company_code companyCode, D.company_name companyName, D.detail_code detailCode, D.tax_code taxCode, D.form_code formCode, D.tag_id tagId, D.tag_name tagName, D.org_code orgCode, D.task_code taskCode, D.tel, D.detail_status detailStatus, R.detail_code detailCode, R.detail_type detailType, R.pictures, R.positive, R.test_tube_no testTubeNo, R.result_status resultStatus, T.task_name taskName FROM biz_environment_task T inner join biz_environment_task_detail D on D.task_code=T.task_code left join biz_environment_task_detail_result R on (D.detail_code = R.detail_code and R.deleted=0) WHERE D.deleted=0
and T.task_code = #{param.taskCode}
and T.task_name like concat('%',#{param.taskName},'%')
and T.task_status = #{param.taskStatus}
and T.start_date <= #{param.startDate}
and T.end_date >= #{param.endDate}
and exists (select 1 from biz_person_task_org o where o.task_code=T.task_code and o.org_code in
#{method.code} )
and T.created_by = #{param.userDataAccount}

select d.task_code as taskCode, d.address as address, d.category as category, d.charge_person as chargePerson, d.charge_person_id_card as chargePersonIdCard, d.city as city, d.company_code as companyCode, d.company_name as companyName, d.created_by as createdBy, d.created_date as createdDate, d.deleted as deleted, d.detail_code as detailCode, d.detail_status as detailStatus, d.form_code as formCode, d.id as id, d.modified_by as modifiedBy, d.modified_date as modifiedDate, d.org_code as orgCode, d.remarks as remarks, d.scope as scope, d.tag_id as tagId, d.tag_name as tagName, d.tax_code as taxCode, d.tel as tel, t.form_status as formStatus from biz_environment_task_detail d left join biz_environment_task_form t on t.deleted='0' and t.task_code = #{param.taskCode} and t.created_by = #{param.userDataAccount} and t.form_code = d.form_code where d.task_code=#{param.taskCode}
and (d.detail_status=#{param.detailStatus} or d.detail_status is null)
and d.detail_status=#{param.detailStatus}
and (d.company_name like CONCAT('%', #{companyNameOrTaxCode},'%') or d.tax_code like CONCAT('%', #{companyNameOrTaxCode},'%'))
order by d.created_date desc,d.modified_date desc
